import { 
    FETCH_PHONE_SUCCESS,
    FETCH_PHONE_FAILURE,
    FETCH_PHONE_START,
    LOAD_MORE_PHONE_SUCCESS,
    LOAD_MORE_PHONE_FAILURE,
    LOAD_MORE_PHONE_START
} from 'actionTypes'
import {
    fetchPhones as fetchPhonesApi,
    loadMorePhones as loadMorePhonesApi,
  } from 'api'
  import {getRenderedPhonesLength} from 'selectors'

export const fetchPhones = () => async dispatch => {
    dispatch({
        type: FETCH_PHONE_START
    })

    try {
        const phones = await fetchPhonesApi()
        dispatch({
            type:FETCH_PHONE_SUCCESS,
            payload: phones
        })
    } catch (err) {
        dispatch({
            type: FETCH_PHONE_FAILURE,
            payload: err,
            error: true
        })
    }

}

export const loadMorePhones = () => async (dispatch, getState) => {
    const offset = getRenderedPhonesLength(getState())
    
    dispatch({
        type: LOAD_MORE_PHONE_START
    })

    try {
        const phones = await loadMorePhonesApi(offset)
        dispatch({
            type:LOAD_MORE_PHONE_SUCCESS,
            payload: phones
        })
    } catch (err) {
        dispatch({
            type: LOAD_MORE_PHONE_FAILURE,
            payload: err,
            error: true
        })
    }

}