import { 
    FETCH_PHONE_SUCCESS,
    FETCH_PHONE_FAILURE,
    FETCH_PHONE_START
} from 'actionTypes'

import {fetchPhones as fetchPhonesApi} from 'api'

export const fetchPhones = () => async dispatch => {
    dispatch({
        type: FETCH_PHONE_START
    })

    try {
        const phones = await fetchPhonesApi()
        dispatch({
            type:FETCH_PHONE_SUCCESS,
            payload: phones
        })
    } catch (err) {
        dispatch({
            type: FETCH_PHONE_FAILURE,
            payload: err,
            error: true
        })
    }

}